import { Button, StyleSheet } from "react-native";
import { SafeAreaProvider } from "react-native-safe-area-context";
import { Text, View } from "../components/Themed";
import { useState } from "react";

import { global } from "../style/styles";
import { changePassword } from "../API/Settings";
import BasicModal from "../components/modal";
import { hidden } from "../style/Hidden";
import { Title } from "../components/Title";
import { GlobalButton } from "../components/Button";
import { BaseButton } from "react-native-gesture-handler";
export default function Setting() {

  const [Open, setOpen] = useState(true)

  const log = ( async () => {
  const res = await changePassword(SafeAreaProvider.Log.Email)
  console.log(res.status)
  if (res.status == 204) {
      setOpen(true)
    }
    else 
      setError(true)
  })


  return (
    <View style={styles.container}>
      <Title title='Settings' subtitle={SafeAreaProvider.Log.Username}></Title>
      <View style={global.middleContainer}>
        <Text>Email : {SafeAreaProvider.Log.Email}</Text>
        <Text>Last Connection : {SafeAreaProvider.Log["Last connection"]}</Text>
        <Text>Acount creation : {SafeAreaProvider.Log["createdAt"]}</Text>
      </View>
      <View style={global.bottomContainer}>
        <GlobalButton title="Modifier le mot de passe" onPress={() => log()}></GlobalButton>
        <GlobalButton title="Open Modal" onPress={() => setOpen(true)}></GlobalButton>
      </View>
      <BasicModal Open={Open} setOpen={setOpen}>
        <GlobalButton title="Open Modal" onPress={() => setOpen(true)}></GlobalButton>
      </BasicModal>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
  },
  title: {
    fontSize: 20,
    fontWeight: "bold",
  },
  separator: {
    marginVertical: 30,
    height: 1,
    width: "80%",
  },
  Title : {
    Flex : 1
  }
});
